import Image from "next/image";
import { Dialog, Transition } from "@headlessui/react";
import { Fragment, useState } from "react";
import { XIcon } from "@heroicons/react/solid";
import { useMoralis, useNFTBalances } from "react-moralis";
import { Card, Skeleton } from "antd";
import { useVerifyMetadata } from "../hooks/useVerifyMetadata";

const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    width: "100%",
    gap: "10px",
  },
};

function Avatar({ changeAvatar, username }) {

  const { user, Moralis, setUserData } = useMoralis();
  let [isOpen, setIsOpen] = useState(false);
  const { data: NFTBalances } = useNFTBalances();
  const { verifyMetadata } = useVerifyMetadata();
  let [src, setSrc] = useState(`https://avatars.dicebear.com/api/identicon/${
    username}.svg`);

  function closeModal() {
    setIsOpen(false);
  }

  function openModal() {
    setIsOpen(true);
  }
  
  return (
    <>
      <Image
        className="rounded-full bg-black cursor-pointer hover:opacity-75"
        src={user.get("profilePic") || src}
        layout="fill"
        onClick={() => changeAvatar && openModal()}
      />
      <Transition appear show={isOpen} as={Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-50 overflow-y-auto"
          onClose={closeModal}
        >
          <div className="min-h-screen px-4 text-center">
            <Transition.Child
              as={Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <Dialog.Overlay className="fixed inset-0" />
            </Transition.Child>

            {/* This element is to trick the browser into centering the modal contents. */}
            <span
              className="inline-block h-screen align-middle"
              aria-hidden="true"
            >
              &#8203;
            </span>
            <Transition.Child
              as={Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0 scale-95"
              enterTo="opacity-100 scale-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100 scale-100"
              leaveTo="opacity-0 scale-95"
            >
              <div className="inline-block w-full max-w-2xl p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-2xl">
                <XIcon
                  onClick={closeModal}
                  className="h-4 w-4 mb-4 cursor-pointer hover:opacity-50"
                />
                <Dialog.Title
                  as="h3"
                  className="text-lg font-medium leading-6 text-gray-900"
                >
                  Choose an NFT:
                </Dialog.Title>
                <div style={{ padding: "15px", maxWidth: "1030px", width: "100%" }}>
      <div style={styles.NFTs}>
        <Skeleton loading={!NFTBalances?.result}>
          {NFTBalances?.result &&
            NFTBalances.result.map((nft, index) => {
              //Verify Metadata
              nft = verifyMetadata(nft);
              return (
                <Card
                  hoverable
                  style={{ width: 200 }}
                  key={index}
                  className="relative rounded-xl hover:opacity-80 cursor-pointer"
                >
                    <div className="border-2 border-[#ec07ee] rounded-sm relative w-[200px] h-[200px]">
                        <Image
                      preview={true}
                      src={nft?.image || "error"}
                      fallback="data:image/png;base64,"
                      alt=""
                      layout="fill"
                    objectFit="cover"
                    onClick={e => setUserData({
                      profilePic: e.target.src,
                    })}
                    />
                    </div>
                  {/* <Meta title={nft.name} /> */}
                </Card>
              );
            })}
        </Skeleton>
      </div>
    </div>
              </div>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition>
    </>
  );
}

export default Avatar;